import { useState } from "react";
import axios from "axios";

function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");

  const handleRegister = async () => {
    try {
      await axios.post("http://localhost:5000/api/auth/register", { name, email, password, role });
      alert("Registration successful! Please login.");
      window.location.href = "/";
    } catch (err) {
      alert("Error registering");
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="w-96 bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-center">üìù Register</h2>
        <input type="text" placeholder="Name" onChange={(e) => setName(e.target.value)} className="w-full p-2 mb-2 border rounded" />
        <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} className="w-full p-2 mb-2 border rounded" />
        <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} className="w-full p-2 mb-2 border rounded" />
        <button onClick={handleRegister} className="w-full bg-green-500 text-white p-2 rounded mt-2">Register</button>
      </div>
    </div>
  );
}

export default Register;
