import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import "../styles/dashboard.css";

function Dashboard() {
    const [stores, setStores] = useState([]);
    const [selectedStore, setSelectedStore] = useState(null);
    const [rating, setRating] = useState(1);
    const [showModal, setShowModal] = useState(false);
    const role = localStorage.getItem("role");

    useEffect(() => {
        if (role === "user") {
            fetchStores();
        } else if (role === "store_owner") {
            fetchOwnerStores();
        }
    }, []);

    const fetchStores = async () => {
        const res = await axios.get("http://localhost:5000/api/stores");
        setStores(res.data);
    };

   


    const submitRating = async () => {
        try {
            const token = localStorage.getItem("token");
            await axios.post(
                "http://localhost:5000/api/ratings",
                { store_id: selectedStore, rating },
                { headers: { Authorization: token } }
            );
            alert("Rating submitted successfully!");
            setShowModal(false);
            fetchStores(); // Refresh store ratings
        } catch (err) {
            alert("Error submitting rating.");
        }
    };

    return (
        <div>
            <Navbar />
            <div className="dashboard-container">
                <h2>📊 {role === "admin" ? "Admin Dashboard" : "User Dashboard"}</h2>

                {role === "user" && (
                    <div>
                        <h3>🏪 All Stores</h3>
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Store Name</th>
                                    <th>Address</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {stores.map((store) => (
                                    <tr key={store.id}>
                                        <td>{store.name}</td>
                                        <td>{store.address}</td>
                                        <td>
                                            <button onClick={() => { setSelectedStore(store.id); setShowModal(true); }}>
                                                Rate Store
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}

                {showModal && (
                    <div className="modal">
                        <div className="modal-content">
                            <h3>Rate Store</h3>
                            <select value={rating} onChange={(e) => setRating(e.target.value)}>
                                <option value="1">⭐</option>
                                <option value="2">⭐⭐</option>
                                <option value="3">⭐⭐⭐</option>
                                <option value="4">⭐⭐⭐⭐</option>
                                <option value="5">⭐⭐⭐⭐⭐</option>
                            </select>
                            <button onClick={submitRating}>Submit Rating</button>
                            <button className="close-modal" onClick={() => setShowModal(false)}>Cancel</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Dashboard;
