import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import "../styles/dashboard.css";

function Dashboard() {
    const [stores, setStores] = useState([]);
    const [reviews, setReviews] = useState([]);
    const role = localStorage.getItem("role");

    useEffect(() => {
        if (role === "user") {
            fetchStores();
        } else if (role === "store_owner") {
            fetchOwnerStores();
        }
    }, []);

    const fetchStores = async () => {
        const res = await axios.get("http://localhost:5000/api/stores");
        setStores(res.data);
    };

    const fetchOwnerStores = async () => {
        try {
            const token = localStorage.getItem("token");
            const res = await axios.get("http://localhost:5000/api/stores/my", {
                headers: { Authorization: token }
            });
            setStores(res.data);
        } catch (err) {
            console.error("Error fetching store:", err);
        }
    };

    return (
        <div>
            <Navbar />
            <div className="dashboard-container">
                <h2>üìä {role === "admin" ? "Admin Dashboard" : "User Dashboard"}</h2>

                {role === "user" && (
                    <div>
                        <h3>üè™ All Stores</h3>
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Store Name</th>
                                    <th>Address</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {stores.map((store) => (
                                    <tr key={store.id}>
                                        <td>{store.name}</td>
                                        <td>{store.address}</td>
                                        <td><button>Rate Store</button></td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}

                {role === "store_owner" && (
                    <div>
                        <h3>üè™ My Store & Reviews</h3>
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Store Name</th>
                                    <th>Address</th>
                                    <th>Rating</th>
                                </tr>
                            </thead>
                            <tbody>
                                {stores.map((store) => (
                                    <tr key={store.id}>
                                        <td>{store.name}</td>
                                        <td>{store.address}</td>
                                        <td>‚≠ê {store.rating || "No ratings yet"}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Dashboard;
