import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // âœ… Import useNavigate
import "../styles/auth.css";

function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate(); // âœ… Use navigate instead of window.location.href

    const handleLogin = async () => {
        try {
            const res = await axios.post("http://localhost:5000/api/auth/login", { email, password });

            localStorage.setItem("token", res.data.token);
            localStorage.setItem("role", res.data.role);

            // âœ… Use navigate instead of window.location.href
            if (res.data.role === "admin") {
                navigate("/admin");
            } else {
                navigate("/dashboard");
            }
        } catch (err) {
            alert("Invalid credentials");
        }
    };

    return (
        <div className="auth-container">
            <h2>ðŸ”‘ Login</h2>
            <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
            <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
            <button onClick={handleLogin}>Login</button>
            <p className="register-text">Don't have an account? <a href="/register">Register here</a></p>
        </div>
    );
}

export default Login;
