import React, { useEffect, useState } from "react";
import axios from "axios";

function Dashboard() {
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ users: 0, stores: 0, ratings: 0 });

  useEffect(() => {
    fetchStores();
    fetchStats();
  }, []);

  const fetchStores = async () => {
    const res = await axios.get("http://localhost:5000/api/stores");
    setStores(res.data);
  };

  const fetchStats = async () => {
    // Fake stats for now, replace with backend stats later
    setStats({ users: 10, stores: 5, ratings: 50 });
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-3xl font-bold mb-6 text-center">📊 Admin Dashboard</h2>

      {/* Stats */}
      <div className="grid grid-cols-3 gap-4 mb-6">
        <div className="p-4 bg-blue-500 text-white rounded-xl text-center shadow-lg">
          <h3 className="text-2xl font-semibold">{stats.users}</h3>
          <p>Total Users</p>
        </div>
        <div className="p-4 bg-green-500 text-white rounded-xl text-center shadow-lg">
          <h3 className="text-2xl font-semibold">{stats.stores}</h3>
          <p>Total Stores</p>
        </div>
        <div className="p-4 bg-yellow-500 text-white rounded-xl text-center shadow-lg">
          <h3 className="text-2xl font-semibold">{stats.ratings}</h3>
          <p>Total Ratings</p>
        </div>
      </div>

      {/* Store List */}
      <h3 className="text-xl font-bold mb-4">🏪 Stores</h3>
      <div className="bg-white p-6 rounded-xl shadow-lg">
        {stores.length === 0 ? (
          <p className="text-gray-500">No stores found.</p>
        ) : (
          <table className="w-full border-collapse">
            <thead>
              <tr className="bg-gray-200">
                <th className="p-3 border">Store Name</th>
                <th className="p-3 border">Address</th>
                <th className="p-3 border">Rating</th>
              </tr>
            </thead>
            <tbody>
              {stores.map((store) => (
                <tr key={store.id} className="border">
                  <td className="p-3">{store.name}</td>
                  <td className="p-3">{store.address}</td>
                  <td className="p-3 text-center">⭐ {store.rating || "No ratings"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
