import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import "../styles/admin.css";

function AdminDashboard() {
    const [users, setUsers] = useState([]);
    const [stores, setStores] = useState([]);
    const [stats, setStats] = useState({ users: 0, stores: 0, ratings: 0 });

    useEffect(() => {
        fetchStats();
        fetchUsers();
        fetchStores();
    }, []);

    const fetchStats = async () => {
        const res = await axios.get("http://localhost:5000/api/admin/stats");
        setStats(res.data);
    };

    const fetchUsers = async () => {
        const res = await axios.get("http://localhost:5000/api/admin/users");
        setUsers(res.data);
    };

    const fetchStores = async () => {
        const res = await axios.get("http://localhost:5000/api/admin/stores");
        setStores(res.data);
    };

    return (
        <div>
            <Navbar />
            <div className="admin-container">
                <h2>üë®‚Äçüíº Admin Dashboard</h2>

                {/* Stats Cards */}
                <div className="dashboard-stats">
                    <div className="stat-box">üë• Users: {stats.users}</div>
                    <div className="stat-box">üè™ Stores: {stats.stores}</div>
                    <div className="stat-box">‚≠ê Ratings: {stats.ratings}</div>
                </div>

                {/* Users Table */}
                <h3>üìú User List</h3>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user.id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.role}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {/* Stores Table */}
                <h3>üè™ Store List</h3>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Store Name</th>
                            <th>Address</th>
                            <th>Owner</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stores.map((store) => (
                            <tr key={store.id}>
                                <td>{store.name}</td>
                                <td>{store.address}</td>
                                <td>{store.owner_name || "Not Assigned"}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default AdminDashboard;
